    public static int whatFloor(String input) {
        String[] inputArray = input.split("");
        int result = 0;
        for (String inputItem : inputArray){
            if (inputItem.equals("(")){
                result++;
            } else {
                result--;
            }
        }
        return result;
    }

    public static int position(String input) {
        String[] inputArray = input.split("");
        int index = 0;
        int counter = 0;
        while (counter != -1) {
            if (inputArray[index].equals("(")){
                counter++;
            } else {
                counter--;
            }
            index++;
        }
        return index;
    }
